import axios from "axios";
import { BASE_URL } from "./settings";

const URLS = {
  SERVICES: BASE_URL + "services"
};

export default class ServiceItemService {
  static getServiceById = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/" + id + "/subcategories";
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static prePublish = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/pre/publish";
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getService = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/" + id;
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getServices = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    let url = URLS.SERVICES + "?limit=200&byAdmin=true";

    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getServicesPendings = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    let url = URLS.SERVICES + "?limit=200&byAdmin=false";

    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static sendFile = async (image, _id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "content-type": "multipart/form-data"
      }
    };
    let url = URLS.SERVICES + "/" + _id + "/images";

    let form = new FormData();
    form.append("image", image);

    try {
      const { data } = await axios.post(url, form, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static addService = async (id, service, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/" + id + "/service";
    try {
      const { data } = await axios.post(url, service, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static deleteService = async (token, id, service) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/" + service + "/service/" + id;

    try {
      const { data } = await axios.delete(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updateService = async (service, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES;

    try {
      const { data } = await axios.put(url, service, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static publish = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES + "/publish/" + id;
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updatePublishedService = async (token, service) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.SERVICES;

    try {
      const { data } = await axios.put(url, service, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };
}
