import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { Col, ListGroup, ListGroupItem, Button, Row } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";

class CategoryList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser()
    };
  }

  render() {
    const { categories, pending } = this.props;
    return (
      <Fragment>
        <Col lg={12}>
          <ListGroup>
            {categories &&
              categories.map((c, index) => {
                return (
                  <ListGroupItem key={index}>
                    <Row>
                      <Col lg={8} md={6} sm={12}>
                        <label>{c.name}</label>
                      </Col>
                      <Col lg={4} md={6} sm={12}>
                        {c.status === true && !pending && (
                          <Button
                            color="danger"
                            onClick={() => this.props.deleteCategory(c._id)}
                          >
                            Off
                          </Button>
                        )}

                        {c.status === false && !pending && (
                          <Button
                            color="info"
                            onClick={() => this.props.updateCategory(c._id)}
                          >
                            On
                          </Button>
                        )}

                        {c.status === true && pending && (
                          <Fragment>
                            <Button
                              color="danger"
                              onClick={() => this.props.declineCategory(c._id)}
                            >
                              Decline
                            </Button>
                            <Button
                              color="info"
                              onClick={() => this.props.acceptCategory(c._id)}
                            >
                              Accept
                            </Button>
                          </Fragment>
                        )}

                        {!pending && (
                          <Button color="primary">
                            <a href={`/categories/${c._id}`}>Edit</a>
                          </Button>
                        )}
                      </Col>
                    </Row>
                  </ListGroupItem>
                );
              })}
          </ListGroup>
        </Col>
      </Fragment>
    );
  }
}

export default connect()(CategoryList);
