import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, Col, Card, CardBody } from "reactstrap";

import { getLoggedInUser } from "../../helpers/authUtils";
import Loader from "../../components/Loader";

import ProductService from "../../services/ProductService";
import ProductList from "./List/ProductList";

import SweetAlert from "react-bootstrap-sweetalert";

class ProductContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser(),
      products: []
    };
  }

  componentDidMount() {
    this.getProducts();
  }

  getProducts() {
    const { token } = this.state.user;
    ProductService.getProducts(token)
      .then(response => {
        this.setState({ products: response.data.result });
      })
      .catch(error => this.setState({ error }));
  }

  //delete product
  deleteThisGoal(id) {
    const getAlert = () => (
      <SweetAlert
        warning
        showCancel
        confirmBtnText="Would you like to remove this Product?"
        cancelBtnText="Cancel"
        confirmBtnBsStyle="danger"
        cancelBtnBsStyle="default"
        title="Are you sure that you want to remove this Product?"
        onConfirm={this.deleteFile}
        onCancel={this.onCancelDelete}
      >
        {/* Você não  */}
      </SweetAlert>
    );

    this.setState({
      alert: getAlert(),
      id: id
    });
  }

  hideAlert = () => {
    // console.log("Hiding alert...");
    this.setState({
      alert: null
    });
  };

  deleteFile = () => {
    let _id = this.state.id;
    const { token } = this.state.user;

    let product = {
      _id,
      published: false
    };

    ProductService.updatePublishedProduct(token, product)
      .then(res => {
        this.getProducts();
      })
      .catch(err => {
        this.getProducts();
        alert("Something wrong happen when we tried to remove this Product.");
      });
    this.setState({
      alert: (
        <SweetAlert success title="Deleted" onConfirm={this.hideAlert}>
          Success to remove the Product
        </SweetAlert>
      ),
      id: ""
    });
  };

  onCancelDelete = () => {
    this.setState({
      alert: (
        <SweetAlert danger title="Cancelled" onConfirm={this.hideAlert}>
          Product is still available
        </SweetAlert>
      )
    });
  };

  onConfirmModal(id) {
    this.deleteFile(id);
  }

  updateService = id => {
    let _id = id;
    const { token } = this.state.user;

    let product = {
      _id,
      published: true
    };

    ProductService.updatePublishedProduct(token, product)
      .then(res => {
        this.getProducts();
      })
      .catch(err => {
        this.getProducts();
      });
  };

  render() {
    const { products } = this.state;
    return (
      <section className="product">
        {/* preloader */}
        {this.props.loading && <Loader />}
        <Row>
          <Col>
            <div className="page-title-box">
              <Row>
                <Col lg={7}>
                  <h4 className="page-title">
                    Welcome, {this.state.user.firstName}
                  </h4>
                </Col>
                <Col lg={5} className="mt-lg-3 mt-md-0"></Col>
              </Row>
            </div>
          </Col>
        </Row>
        <Row>
          <Col lg={12}>
            <Card>
              <CardBody>
                <h3>Products</h3>
              </CardBody>
              <Row>
                <ProductList
                  products={products}
                  deleteProduct={this.deleteThisGoal}
                  updateProduct={this.updateProduct}
                />
              </Row>
            </Card>
          </Col>
        </Row>
      </section>
    );
  }
}

export default connect()(ProductContainer);
