                        {c.published === true ? (
                          <Button
                            color="danger"
                            onClick={() => this.props.deleteService(c._id)}
                          >
                            Off
                          </Button>
                        ) : (
                          <Button
                            color="info"
                            onClick={() => this.props.updateService(c._id)}
                          >
                            On
                          </Button>
                        )}
                      </Col>
                    </Row>
                  </ListGroupItem>
                );
              })}
          </ListGroup>
        </Col>
      </Fragment>
    );
  }
}

export default connect()(ProductList);
