import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

import PerfectScrollbar from "react-perfect-scrollbar";
import "react-perfect-scrollbar/dist/css/styles.css";
import MetisMenu from "metismenujs/dist/metismenujs";

const SideNavContent = () => {
  return (
    <React.Fragment>
      <div id="sidebar-menu">
        <ul className="metismenu" id="side-menu">
          {/* <li className="menu-title">Navigation</li> */}

          <li>
            <Link
              to="/"
              className="waves-effect has-dropdown"
              aria-expanded="true"
            >
              <i className="fe-airplay"></i>
              {/* <span className="badge badge-success badge-pill float-right">
                2
              </span> */}
              <span> Categories </span>
            </Link>
            <ul className="nav-second-level" aria-expanded="false">
              <li>
                <Link to="/categories-create" className="side-nav-link-ref">
                  Create
                </Link>
              </li>
              <li>
                <Link to="/categories-list" className="side-nav-link-ref">
                  List
                </Link>
              </li>
              <li>
                <Link to="/categories-pendings" className="side-nav-link-ref">
                  Categories pendings
                </Link>
              </li>
            </ul>
          </li>

          <li>
            <Link
              to="/"
              className="waves-effect has-dropdown"
              aria-expanded="true"
            >
              <i className="fe-box"></i>
              <span> Products </span>
            </Link>
            <ul className="nav-second-level" aria-expanded="false">
              <li>
                <Link to="/product-list" className="side-nav-link-ref">
                  List
                </Link>
              </li>
              <li>
                <Link to="/product-flagged" className="side-nav-link-ref">
                  Flagged products
                </Link>
              </li>
            </ul>
          </li>

          <li>
            <Link
              to="/"
              className="waves-effect has-dropdown"
              aria-expanded="true"
            >
              <i className="fe-briefcase"></i>
              {/* <span className="badge badge-success badge-pill float-right">
                2
              </span> */}
              <span> Service </span>
            </Link>
            <ul className="nav-second-level" aria-expanded="false">
              <li>
                <Link to="/service-create" className="side-nav-link-ref">
                  Create
                </Link>
              </li>
              <li>
                <Link to="/service-list" className="side-nav-link-ref">
                  List
                </Link>
              </li>
              <li>
                <Link to="/service-pendings" className="side-nav-link-ref">
                  Services pendings
                </Link>
              </li>
            </ul>
          </li>

          <li>
            <Link
              to="/"
              className="waves-effect has-dropdown"
              aria-expanded="true"
            >
              <i className="fe-clipboard"></i>
              <span> Service Types </span>
            </Link>
            <ul className="nav-second-level" aria-expanded="false">
              <li>
                <Link to="/service-types-create" className="side-nav-link-ref">
                  Create
                </Link>
              </li>
              <li>
                <Link to="/service-types-list" className="side-nav-link-ref">
                  List
                </Link>
              </li>
            </ul>
          </li>

          <li>
            <Link to="/" className="waves-effect has-dropdown">
              <i className="fe-users"></i>
              <span> Users</span>
            </Link>
            <ul className="nav-second-level" aria-expanded="false">
              <li>
                <Link to="/users" className="side-nav-link-ref">
                  List
                </Link>
              </li>
            </ul>
          </li>
        </ul>
      </div>
      <div className="clearfix"></div>
    </React.Fragment>
  );
};

class Sidebar extends Component {
  constructor(props) {
    super(props);
    this.handleOtherClick = this.handleOtherClick.bind(this);
    this.initMenu = this.initMenu.bind(this);
  }

  /**
   * Bind event
   */
  componentWillMount = () => {
    document.addEventListener("mousedown", this.handleOtherClick, false);
  };

  /**
   *
   */
  componentDidMount = () => {
    this.initMenu();
  };

  /**
   * Component did update
   */
  componentDidUpdate = prevProps => {
    if (this.props.isCondensed !== prevProps.isCondensed) {
      if (prevProps.isCondensed) {
        document.body.classList.remove("sidebar-enable");
        document.body.classList.remove("enlarged");
      } else {
        document.body.classList.add("sidebar-enable");
        const isSmallScreen = window.innerWidth < 768;
        if (!isSmallScreen) {
          document.body.classList.add("enlarged");
        }
      }

      this.initMenu();
    }
  };

  /**
   * Bind event
   */
  componentWillUnmount = () => {
    document.removeEventListener("mousedown", this.handleOtherClick, false);
  };

  /**
   * Handle the click anywhere in doc
   */
  handleOtherClick = e => {
    if (this.menuNodeRef.contains(e.target)) return;
    // else hide the menubar
    document.body.classList.remove("sidebar-enable");
  };

  /**
   * Init the menu
   */
  initMenu = () => {
    // render menu
    new MetisMenu("#side-menu");
    var links = document.getElementsByClassName("side-nav-link-ref");
    var matchingMenuItem = null;
    for (var i = 0; i < links.length; i++) {
      if (this.props.location.pathname === links[i].pathname) {
        matchingMenuItem = links[i];
        break;
      }
    }

    if (matchingMenuItem) {
      matchingMenuItem.classList.add("active");
      var parent = matchingMenuItem.parentElement;

      /**
       * TODO: This is hard coded way of expading/activating parent menu dropdown and working till level 3.
       * We should come up with non hard coded approach
       */
      if (parent) {
        parent.classList.add("active");
        const parent2 = parent.parentElement;
        if (parent2) {
          parent2.classList.add("in");
        }
        const parent3 = parent2.parentElement;
        if (parent3) {
          parent3.classList.add("active");
          var childAnchor = parent3.querySelector(".has-dropdown");
          if (childAnchor) childAnchor.classList.add("active");
        }

        const parent4 = parent3.parentElement;
        if (parent4) parent4.classList.add("in");
        const parent5 = parent4.parentElement;
        if (parent5) parent5.classList.add("active");
      }
    }
  };

  render() {
    const isCondensed = this.props.isCondensed || false;

    return (
      <React.Fragment>
        <div className="left-side-menu" ref={node => (this.menuNodeRef = node)}>
          {!isCondensed && (
            <PerfectScrollbar>
              <SideNavContent />
            </PerfectScrollbar>
          )}
          {isCondensed && <SideNavContent />}
        </div>
      </React.Fragment>
    );
  }
}

export default connect()(Sidebar);
