import axios from "axios";
import { BASE_URL } from "./settings";

const URLS = {
  CATEGORIES: BASE_URL + "categories",
  REQUEST_CATEGORIES: BASE_URL + "request/categories"
};

export default class CategoryService {
  static addCategory = async (category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES;
    try {
      const { data } = await axios.post(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getCategories = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    try {
      const { data } = await axios.get(URLS.CATEGORIES, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getCategory = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id;
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updateCategory = async (category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES;

    try {
      const { data } = await axios.put(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static deleteCategory = async (token, id) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id;

    try {
      const { data } = await axios.delete(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updateStatusService = async (token, category) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES;

    try {
      const { data } = await axios.put(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getRequestCategories = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    try {
      const { data } = await axios.get(URLS.REQUEST_CATEGORIES, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static accept = async (token, id) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.REQUEST_CATEGORIES + "/accept/" + id;

    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static decline = async (token, id) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.REQUEST_CATEGORIES + "/decline/" + id;

    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };
}
