import axios from "axios";
import { BASE_URL } from "./settings";

const URLS = {
  PRODUCTS: BASE_URL + "products"
};

export default class ProductService {
  static getProducts = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    try {
      const { data } = await axios.get(URLS.PRODUCTS, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getProductsFlagged = async token => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };

    let url = URLS.PRODUCTS + "flags";

    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getProduct = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.PRODUCTS + "/" + id;
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updatePublishedProduct = async (token, product) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.products;

    try {
      const { data } = await axios.put(url, product, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };
}
