import React, { Component } from "react";
// import axios from "axios";
import Dropzone from "react-dropzone";
import { connect } from "react-redux";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";
import Loader from "../../../components/Loader";
import ServiceItemService from "../../../services/ServiceItemService";
import ServiceTypeService from "../../../services/ServiceTypeService";

class ServiceForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser(),
      _id: "",
      title: "",
      phone: "",
      typeServiceId: "",
      address: "",
      opened: [
        {
          day: "MON",
          hours: "08:00 - 18:00"
        },
        {
          day: "TUE",
          hours: "08:00 - 18:00"
        },
        {
          day: "WED",
          hours: "08:00 - 18:00"
        },
        {
          day: "THU",
          hours: "08:00 - 18:00"
        },
        {
          day: "FRI",
          hours: "08:00 - 18:00"
        },
        {
          day: "SAT",
          hours: "08:00 - 18:00"
        },
        {
          day: "SUN",
          hours: "08:00 - 18:00"
        }
      ],
      published: false,
      image: {},
      file: "",
      serviceTypes: [],
      isFetchingPublish: false,
      errorPublish: null,
      successMsgPublish: null,
      img: ""
    };
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {
    this.getServiceTypes();
  }

  getServiceTypes() {
    const { token } = this.state.user;
    ServiceTypeService.getServiceTypes(token)
      .then(response => {
        this.setState({ serviceTypes: response.result });
      })
      .catch(error => this.setState({ error }));
  }

  onDrop = acceptedFiles => {
    this.setState({ file: acceptedFiles[0] }, () => {
      this.sendFile();
    });
  };

  handleTime = (day, value, index) => {
    const newList = this.state.opened.slice();
    newList[index].hours = value;

    this.setState({ list: newList }, () => {
      // console.log("this.state.list", this.state.list);
    });
  };

  onChange(e) {
    this.setState({ file: e.target.files[0] });
  }

  sendFile = () => {
    const { file, _id } = this.state;
    const { token } = this.state.user;

    ServiceItemService.sendFile(file, _id, token)
      .then(response => {
        this.setState({ img: response.data.path });
      })
      .catch(error => {
        // console.log("error", error);
      });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.handleSubmit(this.state);
  };

  publish = () => {
    const { _id } = this.state;
    const { token } = this.state.user;

    this.setState({
      isFetchingPublish: true,
      errorPublish: null,
      successMsgPublish: null
    });

    ServiceItemService.publish(_id, token)
      .then(response => {
        if (response.status === true) {
          let message = "Service was successfully published.";
          this.setState({
            successMsgPublish: message,
            isFetchingPublish: false
          });
        } else {
          this.setState({
            errorPublish: "Error",
            isFetchingPublish: false,
            successMsgPublish: false
          });
        }
      })
      .catch(error => {
        this.setState({ error: error, isFetching: false, successMsg: false });
      });
  };

  componentWillReceiveProps(nextProps) {
    if (nextProps.edit) {
      const {
        _id,
        title,
        phone,
        typeServiceId,
        address,
        opened,
        image,
        published
      } = nextProps.service;

      this.setState({
        _id,
        title,
        phone,
        typeServiceId: typeServiceId && typeServiceId._id,
        address,
        opened,
        image,
        published
      });
    }

    if (nextProps.add) {
      const { _id } = nextProps;

      this.setState({ _id });
    }
  }

  render() {
    const { serviceTypes, opened, image, published, img } = this.state;
    const { isFetching } = this.props;
    return (
      <Form>
        <FormGroup>
          {image && image.path && (
            <img
              alt="service "
              src={`http://192.241.137.188:3055${image.path}`}
              style={{
                margin: "30px 0",
                border: "1px solid #ededed",
                width: "100%"
              }}
            />
          )}
          {img !== "" && (
            <img
              alt="service "
              src={`http://192.241.137.188:3055${img}`}
              style={{
                margin: "30px 0",
                border: "1px solid #ededed",
                width: "100%"
              }}
            />
          )}
          <div className="files">
            <Dropzone
              onDrop={this.onDrop}
              accept="image/jpg, image/jpeg"
              className="drop"
            >
              {({
                getRootProps,
                getInputProps,
                isDragActive,
                isDragReject
              }) => (
                <div {...getRootProps()}>
                  <input {...getInputProps()} />
                  {!isDragActive && "Click here or drop a file to upload!"}
                  {isDragActive && !isDragReject && "Drop it like it's hot!"}
                  {isDragReject && "File type not accepted, sorry!"}
                </div>
              )}
            </Dropzone>
          </div>
          <hr />
          <Label for="title">Title</Label>
          <Input
            type="text"
            name="title"
            id="title"
            placeholder="Title"
            value={this.state.title}
            onChange={e =>
              this.setState({
                title: e.target.value
              })
            }
          />
        </FormGroup>
        <FormGroup>
          <Label for="phone">Phone</Label>
          <Input
            type="text"
            name="phone"
            id="phone"
            placeholder="Phone"
            value={this.state.phone}
            onChange={e =>
              this.setState({
                phone: e.target.value
              })
            }
          />
        </FormGroup>
        <FormGroup>
          <Label for="address">Address</Label>
          <Input
            type="text"
            name="address"
            id="address"
            placeholder="Address"
            value={this.state.address}
            onChange={e =>
              this.setState({
                address: e.target.value
              })
            }
          />
        </FormGroup>
        <FormGroup>
          <Label for="typeServiceId">Service type</Label>
          <Input
            type="select"
            name="typeServiceId"
            id="typeServiceId"
            onChange={e => this.setState({ typeServiceId: e.target.value })}
            value={this.state.typeServiceId}
          >
            <option value="">Choose:</option>
            {serviceTypes &&
              serviceTypes.map((s, index) => {
                return (
                  <option key={index} value={s._id}>
                    {s.name}
                  </option>
                );
              })}
          </Input>

          <h3>Opened</h3>
          <ul>
            {opened &&
              opened.map((m, index) => {
                return (
                  <li key={index}>
                    <div className="day">{m.day}</div>
                    <input
                      value={m.hours}
                      onChange={e => this.handleTime(m, e.target.value, index)}
                    />
                  </li>
                );
              })}
          </ul>
        </FormGroup>

        <div className="submit">
          <Button onClick={this.handleSubmit}>
            {isFetching ? <Loader /> : "Submit"}
          </Button>
        </div>

        {this.props.error && (
          <div
            className="alert alert-danger text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.props.error}</p>
          </div>
        )}

        {this.props.successMsg && (
          <div
            className="alert alert-success text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.props.successMsg}</p>
          </div>
        )}

        <hr />

        {this.props.successMsg && !published && (
          <div className="submit">
            <Button onClick={() => this.publish()}>
              {this.state.isFetchingPublish ? <Loader /> : "Publish"}
            </Button>
          </div>
        )}

        {this.state.errorPublish && (
          <div
            className="alert alert-danger text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.state.errorPublish}</p>
          </div>
        )}

        {this.state.successMsgPublish && (
          <div
            className="alert alert-success text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.state.successMsgPublish}</p>
          </div>
        )}
      </Form>
    );
  }
}

export default connect()(ServiceForm);
