import axios from "axios";
import { BASE_URL } from "./settings";

const URLS = {
  CATEGORIES: BASE_URL + "categories"
};

export default class SubCategoryService {
  static getSubCategoriesById = async (id, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id + "/subcategories";
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static getSubCategory = async (id, category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + category + "/subcategories/" + id;
    try {
      const { data } = await axios.get(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static addSubCategory = async (id, category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id + "/subcategories";
    try {
      const { data } = await axios.post(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static deleteSubCategory = async (token, id, category) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + category + "/subcategories/" + id;

    try {
      const { data } = await axios.delete(url, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updateSubCategory = async (id, category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id + "/subcategories";

    try {
      const { data } = await axios.put(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };

  static updateStatusSubCategory = async (id, category, token) => {
    let config = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    let url = URLS.CATEGORIES + "/" + id + "/subcategories";

    try {
      const { data } = await axios.put(url, category, config);
      return data;
    } catch (err) {
      return err;
    } finally {
      // this.setState({ loading: false });
    }
  };
}
