import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { Col, ListGroup, ListGroupItem, Row } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";

class ProductItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser()
    };
  }

  render() {
    const { product } = this.props;
    return (
      <Fragment>
        <Col lg={12}>
          <ListGroup>
            {/* {products &&
              products.map((p, index) => {
                return ( */}
            <ListGroupItem>
              <Row>
                <Col lg={12} md={12} sm={12}>
                  <label>Name: {product.name}</label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>
                    Category:{" "}
                    {product && product.categoryId && product.categoryId.name}
                  </label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>
                    SubCategory:{" "}
                    {product &&
                      product.subcategoryId &&
                      product.subcategoryId.name}
                  </label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>
                    City:{" "}
                    {product && product.current && product.current.locality},{" "}
                    {product && product.current && product.current.countryName}
                  </label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>
                    Keywords:{" "}
                    {product &&
                      product.keywords &&
                      product.keywords.map(p => {
                        return p;
                      })}
                  </label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>
                    Images: <br />
                    {product &&
                      product.images &&
                      product.images.map((p, index) => {
                        return (
                          <img
                            key={index}
                            src={`http://192.241.137.188:3055${p.path}`}
                            alt="product"
                          />
                        );
                      })}
                  </label>
                </Col>
              </Row>
            </ListGroupItem>
            {/* );
              })} */}
          </ListGroup>
        </Col>
      </Fragment>
    );
  }
}

export default connect()(ProductItem);
