import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import {
  Button,
  FormFeedback,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ListGroup,
  ListGroupItem,
  Row,
  Col
} from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";
import Loader from "../../../components/Loader";

import SweetAlert from "react-bootstrap-sweetalert";

import SubCategoryService from "../../../services/SubCategoryService";

class SubCategorySingleForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser(),
      name: "",
      icon: "",
      modal: false,
      category: "",
      modalName: "",
      modalIcon: "",
      modalId: "",
      modalError: false,
      modalSuccess: false,
      modalFetching: false
    };
    this.handleSubmitSubCategory = this.handleSubmitSubCategory.bind(this);
    this.toggle = this.toggle.bind(this);
  }

  handleSubmitSubCategory(e) {
    e.preventDefault();
    this.props.addSubCategory(this.state);
  }

  //delete document
  deleteThisGoal(id) {
    const getAlert = () => (
      <SweetAlert
        warning
        showCancel
        confirmBtnText="Would you like to remove this SubCategory?"
        cancelBtnText="Cancel"
        confirmBtnBsStyle="danger"
        cancelBtnBsStyle="default"
        title="Are you sure that you want (or wish) to remove this SubCategory?"
        onConfirm={this.deleteFile}
        onCancel={this.onCancelDelete}
      ></SweetAlert>
    );

    this.setState({
      alert: getAlert(),
      id: id
    });
  }

  hideAlert = () => {
    console.log("Hiding alert...");
    this.setState({
      alert: null
    });
  };

  deleteFile = () => {
    let { id, category } = this.state;
    const { token } = this.state.user;

    SubCategoryService.deleteSubCategory(token, id, category)
      .then(res => {
        this.props.getSubCategories();
      })
      .catch(err => {
        alert(
          "Something wrong happen when we tried to remove this SubCategory"
        );
      });
    this.setState({
      alert: (
        <SweetAlert success title="Deletado" onConfirm={this.hideAlert}>
          Success to remove the SubCategory
        </SweetAlert>
      ),
      id: ""
    });
  };

  onCancelDelete = () => {
    this.setState({
      alert: (
        <SweetAlert danger title="Cancelado" onConfirm={this.hideAlert}>
          SubCategory is still available
        </SweetAlert>
      )
    });
  };

  onConfirmModal(id) {
    this.deleteFile(id);
  }

  toggle = value => {
    this.setState({
      modal: !this.state.modal,
      modalError: false,
      modalFetching: false,
      modalSuccess: false
    });

    let id = value;
    const { token } = this.state.user;

    const { category } = this.state;

    SubCategoryService.getSubCategory(id, category, token)
      .then(response => {
        this.setState({
          modalName: response.data.name,
          modalIcon: response.data.icon,
          modalId: id
        });
      })
      .catch(error => {
        // console.log("error", error);
      });
  };

  updateSubCategory = () => {
    const { modalName, modalIcon, modalId, category } = this.state;

    this.setState({
      modalError: false,
      modalFetching: true,
      modalSuccess: false
    });

    const { token } = this.state.user;

    let subcategory = { name: modalName, icon: modalIcon, _id: modalId };

    SubCategoryService.updateSubCategory(category, subcategory, token)
      .then(response => {
        if (response.status === true) {
          this.setState({
            modalError: false,
            modalFetching: false,
            modalSuccess: true
          });
          this.props.getSubCategories();
        } else {
          this.setState({
            modalError: true,
            modalFetching: false,
            modalSuccess: false
          });
        }
      })
      .catch(error => {
        this.setState({
          modalError: true,
          modalFetching: false,
          modalSuccess: false
        });
      });
  };

  close() {
    this.setState({ modal: !this.state.modal });
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.id) {
      this.setState({ category: nextProps.id });
    }
  }

  render() {
    const { name, modal } = this.state;
    const { subcategories } = this.props;
    return (
      <FormGroup className="subCategory">
        <h4>SubCategory</h4>
        <Label for="name">Name</Label>
        <Input
          type="text"
          name="name"
          id="name"
          value={this.state.name}
          onChange={e =>
            this.setState({
              name: e.target.value
            })
          }
        />
        {name !== "" ? (
          <Fragment>
            <FormFeedback valid>That name is available.</FormFeedback>
          </Fragment>
        ) : (
          ""
        )}
        <Label for="icon">Icon</Label>
        <Input
          type="text"
          name="icon"
          id="icon"
          value={this.state.icon}
          onChange={e =>
            this.setState({
              icon: e.target.value
            })
          }
        />
        <div className="submit">
          <Button color="primary" onClick={this.handleSubmitSubCategory}>
            {this.props.isFetchingSub ? <Loader /> : "Add"}
          </Button>
          {this.props.errorSub && (
            <div
              className="alert alert-danger text-center"
              style={{ marginTop: "50px" }}
            >
              <p style={{ margin: "0" }}>{this.props.errorSub}</p>
            </div>
          )}

          {this.props.successMsgSub && (
            <div
              className="alert alert-success text-center"
              style={{ marginTop: "50px" }}
            >
              <p style={{ margin: "0" }}>{this.props.successMsgSub}</p>
            </div>
          )}
        </div>
        <hr />
        <ListGroup>
          {subcategories.length > 0 ? (
            subcategories.map((s, index) => {
              return (
                <ListGroupItem style={{ margin: "0px" }} key={index}>
                  <Row>
                    <Col lg={6} md={6} sm={12}>
                      <label>{s.name}</label>
                    </Col>
                    <Col lg={6} md={6} sm={12}>
                      {s.status === true ? (
                        <Button
                          color="danger"
                          onClick={() => this.props.deleteThisGoal(s._id)}
                        >
                          Off
                        </Button>
                      ) : (
                        <Button
                          color="secondary"
                          onClick={() => this.props.updateProduct(s._id)}
                        >
                          On
                        </Button>
                      )}
                      {/* <Button
                        href="#/"
                        className="btn btn-danger"
                        onClick={() => this.deleteThisGoal(s._id)}
                      >
                        Delete
                      </Button> */}
                      <Button
                        className="btn btn-info"
                        onClick={() => this.toggle(s._id)}
                      >
                        Update
                      </Button>
                    </Col>
                  </Row>
                </ListGroupItem>
              );
            })
          ) : (
            <ListGroupItem>0 subcategories added</ListGroupItem>
          )}
        </ListGroup>
        <hr />
        {this.state.alert}
        <Modal isOpen={modal} toggle={this.toggle}>
          <ModalBody>
            <Label for="modalName">Name</Label>
            <Input
              type="text"
              name="modalName"
              id="modalName"
              value={this.state.modalName}
              onChange={e =>
                this.setState({
                  modalName: e.target.value
                })
              }
            />
            {name !== "" ? (
              <Fragment>
                <FormFeedback valid>That name is available.</FormFeedback>
              </Fragment>
            ) : (
              ""
            )}
            <Label for="modalIcon">Icon</Label>
            <Input
              type="text"
              name="modalIcon"
              id="modalIcon"
              value={this.state.modalIcon}
              onChange={e =>
                this.setState({
                  modalIcon: e.target.value
                })
              }
            />
          </ModalBody>
          <ModalFooter>
            <Button onClick={() => this.setState({ modal: !this.state.modal })}>
              Cancel
            </Button>
            <Button
              color="primary"
              onClick={() => {
                this.updateSubCategory();
              }}
            >
              {this.state.modalFetching ? <Loader /> : "Update"}
            </Button>
          </ModalFooter>
          {this.state.modalError && (
            <div
              className="alert alert-danger text-center"
              style={{ marginTop: "50px" }}
            >
              <p style={{ margin: "0" }}>Error</p>
            </div>
          )}

          {this.state.modalSuccess && (
            <div
              className="alert alert-success text-center"
              style={{ marginTop: "20px" }}
            >
              <p style={{ margin: "0" }}>Success</p>
            </div>
          )}
        </Modal>
      </FormGroup>
    );
  }
}

export default connect()(SubCategorySingleForm);
