import React, { Component } from "react";
import { connect } from "react-redux";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";
import Loader from "../../../components/Loader";

class ServiceTypeForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser(),
      icon: "",
      name: ""
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    this.props.handleSubmit(this.state);
  };

  componentWillReceiveProps(nextProps) {
    if (nextProps.name) {
      this.setState({ name: nextProps.name });
    }
    if (nextProps.icon) {
      this.setState({ icon: nextProps.icon });
    }
  }

  render() {
    const { isFetching } = this.props;
    return (
      <Form>
        <FormGroup>
          <Label for="name">Name</Label>
          <Input
            type="text"
            name="name"
            id="name"
            placeholder="Name"
            value={this.state.name}
            onChange={e =>
              this.setState({
                name: e.target.value
              })
            }
          />
        </FormGroup>
        <FormGroup>
          <Label for="icon">Icon</Label>
          <Input
            type="text"
            name="icon"
            id="icon"
            placeholder="File name"
            value={this.state.icon}
            onChange={e =>
              this.setState({
                icon: e.target.value
              })
            }
          />
        </FormGroup>

        <div className="submit">
          <Button onClick={this.handleSubmit}>
            {isFetching ? <Loader /> : "Submit"}
          </Button>
        </div>

        {this.props.error && (
          <div
            className="alert alert-danger text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.props.error}</p>
          </div>
        )}

        {this.props.successMsg && (
          <div
            className="alert alert-success text-center"
            style={{ marginTop: "20px" }}
          >
            <p style={{ margin: "0" }}>{this.props.successMsg}</p>
          </div>
        )}

        <hr />

        {/* {edit && (
          <Fragment>
            <ServiceForm
              id={this.props.id}
              services={services}
              addServices={this.props.addServices}
              getServices={this.props.getServices}
              errorSub={this.props.errorSub}
              successMsgSub={this.props.successMsgSub}
              isFetchingSub={this.props.isFetchingSub}
            />
          </Fragment>
        )} */}
      </Form>
    );
  }
}

export default connect()(ServiceTypeForm);
