import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { Col, ListGroup, ListGroupItem, Button, Row } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";

class ServiceList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser()
    };
  }

  render() {
    const { services } = this.props;
    return (
      <Fragment>
        <Col lg={12}>
          <ListGroup>
            {services &&
              services.map((c, index) => {
                // console.log("c", c);
                return (
                  <ListGroupItem key={index}>
                    <Row>
                      <Col lg={8} md={6} sm={12}>
                        <label>{c.title}</label>
                      </Col>

                      <Col lg={4} md={6} sm={12}>
                        {c.published === true ? (
                          <Button
                            color="danger"
                            onClick={() => this.props.deleteService(c._id)}
                          >
                            Off
                          </Button>
                        ) : (
                          <Button
                            color="info"
                            onClick={() => this.props.updateService(c._id)}
                          >
                            On
                          </Button>
                        )}

                        <Button color="primary">
                          <a href={`/service/${c._id}`}>Edit</a>
                        </Button>
                      </Col>
                    </Row>
                  </ListGroupItem>
                );
              })}
          </ListGroup>
        </Col>
      </Fragment>
    );
  }
}

export default connect()(ServiceList);
