import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { Col, ListGroup, ListGroupItem, Row } from "reactstrap";

import { getLoggedInUser } from "../../../helpers/authUtils";

class UserItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: getLoggedInUser()
    };
  }

  render() {
    const { details } = this.props;
    let picture = details && details.picture;
    let pattern = /^((http|https):\/\/)/;

    if (!pattern.test(picture)) {
      picture = "http://192.241.137.188:3055/" + picture;
    }

    return (
      <Fragment>
        <Col lg={12}>
          <ListGroup>
            <ListGroupItem>
              <Row>
                <Col lg={12} md={12} sm={12}>
                  <label>Name: {details && details.name}</label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <label>Email: {details && details.email}</label>
                </Col>
                <Col lg={12} md={12} sm={12}>
                  <img src={picture} alt="user" />
                </Col>
              </Row>
            </ListGroupItem>
          </ListGroup>
        </Col>
      </Fragment>
    );
  }
}

export default connect()(UserItems);
